import { useState }  from 'react';

    function BookCreate({ onCreate }) {
        const [title, setTitle] = useState('');

        const handleChange = (event) => {
            setTitle(event.target.value);
        };

        const handleSubmit = (event) => {
            event.preventDefault();
            onCreate(title)
        };

    //With input elements we set the inputs value prop as well as its onChange prop....
    //which means it also needs a piece of state.
    //Form element takes the onSubmit (when user enters or clicks button) and...
    //calls OnCreate which is our prop in being passed in from the App component
    return (
        <div>
        <form onSubmit={handleSubmit}>
            <label>Title</label>
            <input value={title} onChange={handleChange}/>
            <button>Create!</button>
        </form>
        </div>
    )
}
export default BookCreate;
